mapper.update:
/** File.deleteFile */
UPDATE
	TB_FOR_ATCH_FILES
	SET
		IS_DEL='Y', 
		UPDT_ID=#{userId}, 
		UPDT_DATE=now()
WHERE
	FILE_GRP_CD=#{fileGrpCd}

		AND FILE_ID=#{fileId}


		AND FILE_GRP_NUM=#{fileGrpNum}


		AND FILE_GRP_VAL=#{fileGrpVal}

mapper.select:
/** File.selectFileList */

WHERE
	FILE_GRP_CD=#{fileGrpCd} 
	AND IS_DEL='N'

		AND FILE_GRP_NUM=#{fileGrpNum}


		AND FILE_GRP_VAL=#{fileGrpVal}

ORDER BY
	INSRT_DATE DESC

mapper.insert:
/** File.selectFileId - 차후 시퀀스로 변경 */
INSERT INTO SEQ_ATCH_FILES(INSRT_DATE) VALUES(NOW())

	SELECT LAST_INSERT_ID()
