mapper.select:
/** menu.selectMenu */



WHERE
	IS_DEL=#{isDel}
	AND LVL=#{lvl}

		AND MNU_ID=#{mnuId}


		AND PRT_MNU_ID=#{prtMnuId}


		AND IS_INC=#{isInc}


		AND MNU_TYPE=#{mnuType}

LIMIT 1

/** menu.selectMenuList */



WHERE
	IS_DEL=#{isDel}
	AND LVL=#{lvl}

		AND MNU_ID=#{mnuId}


		AND PRT_MNU_ID=#{prtMnuId}


		AND MNU_TYPE=#{mnuType}

ORDER BY
	SORT_NUM
